INSERT INTO `pendiri_kami` (`id_pendiri`, `nama_pendiri`, `deskripsi_pendiri`, `foto_pendiri`, `language`) VALUES
(1, 'Miko Kamal', '<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\"><strong>Dr. Miko Kamal, the Managing Partner of the firm, completed his Doctor of Philosophy (PhD) thesis on &lsquo;The Role of Board of Commissioners in Creating Good Governance of Indonesia&rsquo;s State-owned Enterprises&rsquo; at Macquarie University Australia in 2011</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">Dr. Kamal has also completed a Master of Law (Commercial Law) in 2003 at Deakin University, Australia and a Bachelor of Law (Constitutional Law) in 1996 at Bung Hatta University, Indonesia.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">Dr Kamal has published several international and national academic and professional journals, books, and in printed media. His main focus are the issues of Anti-Corruption Policies and Strategy, Corporate Governance and Boards of Commissioners in State Owned Enterprises. He is a licensed advocate and member of Indonesian Advocates Association (PERADI). He has been practicing as a professional lawyer since 1996.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">In addition to his role as a practice lawyer, he is also a professional lecturer at several universities in Indonesia.</span></span></p>\r\n', '20201123224717201809181619352018091816170120180905080721miko_kamal.png', 'en'),
(2, 'Miko Kamal', '<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\"><strong>Dr. Miko Kamal, Managing Partner perusahaan, menyelesaikan tesis Doctor of Philosophy (PhD) tentang&lsquo;Peran Dewan Komisaris Dalam Mewujudkan Good Governance Indonesia&rsquo;s Badan Usaha Milik Negara&rsquo;di Universitas Macquarie Australia pada tahun 2011</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">Dr. Kamal juga menyelesaikan Magister Hukum (Hukum Niaga) pada tahun 2003 di Deakin University, Australia dan Sarjana Hukum (Hukum Konstitusi) pada tahun 1996 di Universitas Bung Hatta, Indonesia.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">Dr Kamal telah menerbitkan beberapa jurnal akademik dan profesional internasional dan nasional, buku, dan di media cetak. Fokus utamanya adalah masalah Kebijakan dan Strategi Anti Korupsi, Tata Kelola Perusahaan dan Dewan Komisaris di Badan Usaha Milik Negara. Beliau adalah seorang advokat berlisensi dan anggota Perhimpunan Advokat Indonesia (PERADI). Ia telah berpraktik sebagai pengacara profesional sejak tahun 1996.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"font-family:Georgia,serif\">Selain perannya sebagai pengacara praktek, ia juga merupakan dosen profesional di beberapa universitas di Indonesia.</span></span></p>\r\n', '20201123224717201809181619352018091816170120180905080721miko_kamal.png', 'bs');
